sources.style/reset.scss:
/* http://meyerweb.com/eric/tools/css/reset/ 
   v2.0 | 20110126
   License: none (public domain)
*/

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}

sources.style/rules.scss:
:root {
    --co-dark: #000000;
    --co-gray: #858585;
    --co-action: #3043F2;
    --co-actionSecondary: #D6E0F9;
    --co-hover: #1980DD;

    --sz-h1: 32px;
    --sz-h2: 24px;
    --sz-body: 16px;
    --sz-small: 12px;

    --fw-regular: 400;
    --fw-medium: 500;
    --fw-semiBold: 600;

    --ls-small: calc((var(--sz-small) / 100) * 4);
    --ls-medium: calc((var(--sz-small) / 100) * 3);
    --ls-large: calc((var(--sz-small) / 100) * 1);

    --lh-small: 150%;
    --lh-medium: 155%;
    --lh-large: 125%;

    --padding: 16px;

    @media screen and(min-width: 1024px) {
        --sz-h1: 64px;
        --sz-body: 18px;
        --sz-small: 16px;

        --padding: 32px;
    }
}

* {
    box-sizing: border-box;
}

body {
    font-family: -apple-system,system-ui,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif;
    padding: var(--padding);
}

.link {
    text-decoration: none;
    letter-spacing: var(--ls-small);

    &:hover {
        text-decoration: underline;
    }
}

sources.style/gallery.scss:
main {
    max-width: 1680px;
    margin: 20vh auto 0;
    position: relative;
}

.gallery {
    &__label {
        display: inline-block;
        margin-bottom: var(--padding);

        font-size: var(--sz-small);
        color: var(--co-gray);
        letter-spacing: var(--ls-small);
    }
    &__item {
        width: 100%;

        &:not(:last-child) {
            margin-bottom: calc(var(--padding) * 2);

            @media screen and (min-width: 1025px){
                margin-bottom: calc(var(--padding) * 2);    
            }
        }

        &:first-child {
            img {
                background: #989BAC;
            }
        }
    }
    &__image {
        width: 100%;

        @media screen and (min-width: 1025px){
            height: calc(100vh - calc(var(--padding) * 4));
            object-fit: cover;
            object-position: 50% 50%;
        }

        &--min {
            object-fit: contain;
        }
    }
}


