sources.style/header.scss:
/*
*
*
* STYLE
*
*
*/

.header {
    display: flex;
    flex-direction: column;
    align-items: center;

    width: 100%;
    height: 50vh;

    text-align: center;

    &__name {
      font-size: var(--sz-small);
      font-weight: var(--fw-medium);
      letter-spacing: var(--ls-small);
      line-height: var(--lh-small);
    }
    &__tagline {
      font-size: var(--sz-h1);
      font-weight: var(--fw-medium);
      color: var(--co-gray);
      letter-spacing: var(--ls-large);
      line-height: var(--lh-large);
    }
    &__highlights {
      color: var(--co-dark);
    }
    &__descr {
      font-weight: var(--fw-regular);
      letter-spacing: var(--ls-medium);
      line-height: var(--lh-medium);
      color: var(--co-dark);
    }
}




/*
*
*
* LAYOUT
*
*
*/

.header {
    padding-top: 8px;

    &__tagline {
      max-width: 800px;
      width: 100%;
      margin-top: auto;
      margin-bottom: 32px;
    }
}

sources.style/about.scss:
#about {
    margin-top: 104px;
    margin-bottom: 104px;

    @media screen and (min-width: 1025px) {
        margin: 20vh auto 36px;
    }

    .container {
        @media screen and(min-width: 1280px) {
            display: flex;
        }
    }

    .left {
        @media screen and(min-width: 1280px) {
            max-width: calc(calc(100% / 12) * 5);
        }
    }
    .right {
        @media screen and(min-width: 1280px) {
            max-width: calc(calc(100% / 12) * 5);
            margin-left: auto;
            margin-top: 10%;
        }
    }

    h2 {
        font-size: var(--sz-h1);
        font-weight: var(--fw-semiBold);
        letter-spacing: var(--ls-large);
        line-height: var(--lh-large);
    }

    h3 {
        font-size: var(--sz-h2);
        font-weight: var(--fw-medium);
        letter-spacing: var(--ls-large);
        line-height: var(--lh-medium);

        margin-top: 24px;
    }

    .row {
        margin-top: 24px;

        @media screen and (min-width: 768px) {
            display: flex;
            margin-top: 104px;
        }

        p {
            letter-spacing: var(--ls-small);
            line-height: var(--lh-small);

            margin-bottom: 24px;

            @media screen and (min-width: 768px) {
                max-width: calc(50% - 8px);
                margin-bottom: 0;
            }

            &:first-child {
                @media screen and (min-width: 768px) {
                    margin-right: 16px;
                }
            }
        }
    }

    .about__image {
        max-width: 565px;
        width: 100%;
        margin-left: auto;

        @media screen and (max-width: 1025px) {
            display: none;
        }
    }

    .link {
        display: inline-block;
        margin-top: 24px;
    }
}

sources.style/animation.scss:
:root {
    //--cubic: cubic-bezier(0.65, 0, 0.35, 1);
    --cubic: cubic-bezier(0.33, 1, 0.68, 1);
    //cubic-bezier(0.33, 1, 0.68, 1);
}


.header {
    &__name {
        opacity: 0;
        animation: fadeIn .6s linear forwards;
    }
}


.line {
    &__wrapper {
        display: inline-block;
        overflow: hidden;
        max-height: 76px;
    }
    &__inner {
        display: inline-block;
        transform: translateY(100%);
        animation: slideIn 1s var(--cubic) forwards;
    }
}


.header {
    &__tagline {
        .line__wrapper {
            @for $i from 1 through 3 {
                &:nth-child(3n + #{$i}) {
                    .line__inner {
                        animation-delay: $i / 8 + s;

                        @media screen and (max-width: 768px) {
                            animation-delay: 0.4 + $i / 8 + s;
                        }
                    }
                }
            }
        }
    }
}


.header__descr {
    opacity: 0;
    animation: fadeIn .6s linear 1s forwards;
}

.gallery {
    transform: translateY(200px);
    opacity: 0;
    animation: gallery 2s var(--cubic) forwards;
}



@keyframes slideIn {
    from { transform: translateY(80%); }
    to { transform: translateY(0%); }
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

@keyframes gallery {
    from { transform: translateY(200px); opacity: 0; }
    to { transform: translateY(0px); opacity: 1; }
}

sources.style/footer.scss:
footer {
    position: relative;
    display: flex;
    align-content: center;
    justify-content: center;

    margin: calc(var(--padding) * 1) 0 0 0;
}

.contact-cta {
    transition: all .3s;

    &--is-fixed {
        position: fixed;
        bottom: 32px; left: 50%;
        transform: translateX(-50%) translateY(100px);
        z-index: 10;

        @media screen and (max-width: 1024px){
            bottom: 16px;
        }
    }
    &--is-visible {
        transform: translateX(-50%) translateY(0px);
    }
    &--is-bottom {
        transform: translateX(calc(-50% - 20px)) translateY(0px);
        border-radius: 4px 0 0 4px !important;
    }
    &--ghost {
        position: static;
        opacity: 0;
        z-index: 0;
    }
}

.scrollTop {
    padding: 8px 14px !important;
    border-radius: 0 4px 4px 0 !important;

    background-color: var(--co-actionSecondary) !important;
    color: var(--co-action) !important;

    @media screen and (max-width: 1024px){
        display: none !important;
    }
}

sources.style/buttons.scss:
.button {
    display: inline-block;
    background-color: var(--co-action);
    color: #fff;

    padding: 8px 80px;
    border-radius: 4px;

    text-decoration: none;

    font-size: var(--sz-small);
    font-weight: var(--fw-medium);
    letter-spacing: var(--ls-small);
    line-height: var(--lh-small);

    &:hover {
        background-color: var(--co-hover);
    }

    @media screen and (max-width: 1024px){
        width: calc(100% - var(--padding));
        text-align: center;
        padding: 16px 80px;
    }

    @media screen and (max-width: 768px){
        padding: 12px 80px;
    }
}
